import React from 'react';
import {useSelector, useDispatch} from 'react-redux';
import { Link } from 'react-router-dom';
import Rating from '../componenets/Rating';
import LoadingBox from '../componenets/LoadingBox';
import Messagebox from '../componenets/Messagebox';

export default function ProductScreen(props) {
  const dispatch = useDispatch();
  const productId = props.match.params.id;
  const productDeltails = useSelector(state => state.productDeltails);
  const {loading,error,product} = productDeltails;

  useEffect (() => {
    dispatch(detailsProduct(productId));
  }, [productId]);

  return (

    <div>
    {loading ? (<LoadingBox></LoadingBox>):
    error ? (<Messagebox varient = "danger">{error}</Messagebox>):

    <div>
    <Link to="/">Back to result</Link>
    <div className="row top">
      <div className="col-2">
        <img className="large" src={product.image} alt={product.name}></img>
      </div>
      <div className="col-1">
        <ul>
          <li>
            <h1>{product.name}</h1>
          </li>
          <li>
            <Rating
              rating={product.rating}
              numReviews={product.numReviews}
            ></Rating>
          </li>
          <li>Pirce : ${product.price}</li>
          <li>
            Description:
            <p>{product.description}</p>
          </li>
        </ul>
      </div>
      <div className="col-1">
        <div className="card card-body">
          <ul>
            <li>
              <div className="row">
                <div>Price</div>
                <div className="price">${product.price}</div>
              </div>
            </li>
            <li>
              <div className="row">
                <div>Status</div>
                <div>
                  {product.countInStock > 0 ? (
                    <span className="success">In Stock</span>
                  ) : (
                    <span className="error">Unavailable</span>
                  )}
                </div>
              </div>
            </li>
            <li>
              <button className="primary block">Add to Cart</button>
            </li>
          </ul>
        </div>
      </div>
    </div>
  </div>

}

</div>


  );
}
